Import-Module ActiveDirectory 
Import-Csv C:\loginy.csv | ForEach-Object {New-ADUser -Name $_.login -DisplayName $_.login  -UserPrincipalName $_.login -AccountPassword (ConvertTo-SecureString $_.password -AsPlainText -Force) -PasswordNeverExpires $true -Path "OU=ludzie,DC=pszemo,DC=local"  -Enabled $true  -ChangePasswordAtLogon $false }