$Storage = Get-ChildItem F:\ -Directory -Recurse | Where-Object {(Get-Acl $_.FullName).Access | Where-Object {($_.IdentityReference -eq 'ttit\ginalskak') -and $_.IsInherited -eq $false} }
$Storage | ForEach-Object -Process {$acc = (Get-Acl $_.FullName).Access | Where-Object {($_.IdentityReference -eq 'ttit\ginalskak')};$FN = $_.FullName ;$AC = $acc.FileSystemRights; $AT = $acc.AccessControlType ;echo "$FN .... ($AT/$AC)"} > C:\temp\ginalska.txt